mapper.insert:
insert proxy
( id, gmt_create, gmt_modified, host, avg_cost, success_rate, source, attributes,failed_count,user_name,password)
values
( #{id}, now() ,now(), #{host}, 0 , 0, #{source}, #{attributes},0,#{userName},#{password})
ON DUPLICATE KEY UPDATE
gmt_modified=now()
,avg_cost=#{avgCost}
,success_rate=#{successRate}
,failed_count=0
,failed_count=failed_count+#{failedCount}
,user_name=#{userName}
,password=#{password}

mapper.select:
select host,user_name,password,attributes from proxy where
 success_rate >= #{successRate} 
order by success_rate desc ,avg_cost asc ,failed_count asc
limit #{length}

select id,host from proxy where

  id > #{id}
  and  failed_count<#{maxFailedCount}

order by id asc
limit #{length}
